import { atom } from 'recoil'
import { Payment, Category } from '../interfaces/payment'

const categories = [
    {
        id: 1,
        name: 'food',
    },
    {
        id: 2,
        name: 'lent',
    },
    {
        id: 3,
        name: 'tranceportation',
    },
    {
        id: 3,
        name: 'tranceportation',
    },
]

const payments = [
    {
        paymentName: 'lunch',
        cost: 2500,
        category: 'food',
        payedById: 1,
        date: '2022-07-01',
    },
    {
        paymentName: 'train',
        cost: 2000,
        category: 'transportation',
        payedById: 2,
        date: '2022-07-02',
    },
    {
        paymentName: 'gass',
        cost: 1000,
        category: 'transportation',
        payedById: 2,
        date: '2022-07-05',
    },
    {
        paymentName: 'movie',
        cost: 1500,
        category: 'free',
        payedById: 1,
        date: '2022-07-09',
    },
    {
        paymentName: 'omochi',
        cost: 120,
        category: 'food',
        payedById: 1,
        date: '2022-07-09',
    },
]

export const paymentState = atom<Payment[]>({
    key: 'paymentState',
    default: payments.map((payment: any, index: number) => (
    {
        id: index,
        paymentName: payment.paymentName,
        cost: payment.cost,
        category: payment.category,
        payedById: payment.payedById,
        date: payment.date,
        status: 'personal'
    })),
})

export const emptyPayments = atom<Payment[]>({
    key: 'emptyPayment',
    default:[
        {
            id: paymentState.length,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 0,
            date: '',
            status: 'personal',
        },
        {
            id: -2,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 1,
            date: '',
            status: 'personal',
        },   
    ]
})

export const defaultEmptyPayments = atom<Payment[]>({
    key: 'defaultEmptyPayments',
    default:[
        {
            id: -1,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 0,
            date: '',
            status: 'personal',
        },
        {
            id: -2,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 1,
            date: '',
            status: 'personal',
        },   
    ]
})

export const categoryState = atom<Category[]>({
    key: 'categoryState',
    default: categories.map((category) => (
    {
        id: category.id,
        name: category.name,
    })),
})

export const names = atom<string[]>({
    key: 'name1',
    default: ['Yudai', 'Ami'],
})
