import { useRecoilState, useRecoilValue } from 'recoil'
import { categoryState, paymentState } from '../recoil/atoms'
import { Payment } from '../interfaces/payment'
import { useNavigate } from 'react-router-dom'
import { Button, TextField, Typography } from '@mui/material'
import {  
    TableContainer, 
    Table,
    TableHead, 
    TableBody, 
    TableRow, 
    TableCell,
    Checkbox
} from '@mui/material'
import { Paper } from '@mui/material'
import { Box, Grid } from '@mui/material'
import { margin } from '@mui/system'


function Test() {
    const name1 = 'Yudai'
    const name2 = 'Ami'

    const [payments, setPayments] = useRecoilState(paymentState)
    const navigate = useNavigate()

    const payments1 = payments.filter(payment => payment.payedById === 1)
    const payments2 = payments.filter(payment => payment.payedById === 2)

    const GoCreatePage = () => {
        console.log(payments)
        navigate("../create")
    }

    const GoAllPage = () => {
        navigate("../all")
    }

    const handle = (payment: Payment) => {
        if(payment.status === 'personal'){
            const newPayment = {...payment, status: 'common'}
            const newPayments = [...payments.slice(0,payment.id), newPayment, ...payments.slice(payment.id+1)]
            setPayments(newPayments)
        }
        if(payment.status === 'common'){
            const newPayment = {...payment, status: 'personal'}
            const newPayments = [...payments.slice(0,payment.id), newPayment, ...payments.slice(payment.id+1)]
            setPayments(newPayments)
        }
        console.log(payments)
    }

    const Calculation = () => {
        const commonPayments1 = payments1.filter((payment) => payment.status === 'common')
        const commonPayments2 = payments2.filter((payment) => payment.status === 'common')

        let tmpCommonPaymentsCost1 = 0
        commonPayments1.map((payment) => {
            tmpCommonPaymentsCost1 = tmpCommonPaymentsCost1 + payment.cost
        })
        const commonPaymentsCost1 = tmpCommonPaymentsCost1

        let tmpCommonPaymentsCost2 = 0
        commonPayments2.map((payment) => {
            tmpCommonPaymentsCost2 = tmpCommonPaymentsCost2 + payment.cost
        })
        const commonPaymentsCost2 = tmpCommonPaymentsCost2

        const give2to1 = commonPaymentsCost1 * 0.5
        const give1to2 = commonPaymentsCost2 * 0.5

        const calculatedGive2to1 = give2to1 - give1to2

        if(calculatedGive2to1 > 0) {
            return(
                <>
                    <Typography variant='h3'>{name2} → {name1} : {calculatedGive2to1} 円</Typography>
                </>
            )
        }
        if(calculatedGive2to1 === 0) {
            return(
                <>
                    <Typography variant='h3'>二人とも同額支払っています</Typography>
                </>
            )
        }
        if(calculatedGive2to1 < 0) {
            return(
                <>
                    <Typography variant='h3'>{name1} → {name2} : {calculatedGive2to1*(-1)} 円</Typography>
                </>
            )
        }
    }

    return(
        <>
        {/* Calculation */}
        <Box sx={{textAlign: "center"}}>
            {Calculation()}
        </Box>

        {/* Table */}
        <Box sx={{
            textAlign: "center",
            backgroundColor: "blue"
        }}>
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <Typography variant='h4'> {name1} </Typography>
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>共通の物</TableCell>
                                    <TableCell>用途</TableCell>
                                    <TableCell align="right">分類</TableCell>
                                    <TableCell align="right">金額</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {payments1.map((payment) => (
                                    //tableChack(payment)
                                    <TableRow
                                        key={payment.paymentName}
                                    >
                                        <TableCell>
                                            <Checkbox 
                                                onChange={()=>handle(payment)}
                                            />
                                        </TableCell>
                                        <TableCell>{payment.paymentName}</TableCell>
                                        <TableCell align="right">{payment.category}</TableCell>
                                        <TableCell align="right">{payment.cost}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Grid>
                <Grid item xs={6}>
                    <Typography variant='h4'> {name2} </Typography>
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>共通の物</TableCell>
                                    <TableCell>用途</TableCell>
                                    <TableCell align="right">分類</TableCell>
                                    <TableCell align="right">金額</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {payments2.map((payment) => (
                                    //tableChack(payment)
                                    <TableRow
                                        key={payment.paymentName}
                                    >
                                        <TableCell>
                                            <Checkbox 
                                                onChange={()=>handle(payment)}
                                            />
                                        </TableCell>
                                        <TableCell>{payment.paymentName}</TableCell>
                                        <TableCell align="right">{payment.category}</TableCell>
                                        <TableCell align="right">{payment.cost}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Grid>
            </Grid>
        </Box>
        
        </>
    )
}

export default Test


