import { atom } from 'recoil'
import { Payment, Category } from '../interfaces/payment'

const categories = [
    {
        id: 1,
        name: 'food',
    },
    {
        id: 2,
        name: 'lent',
    },
    {
        id: 3,
        name: 'tranceportation',
    },
    {
        id: 3,
        name: 'tranceportation',
    },
]

/*
const payments = [
    {
        paymentName: 'キャベツ',
        cost: 130,
        category: '食費',
        payedById: 0,
        date: '2022-07-01',
    },
    {
        paymentName: '高速道路代',
        cost: 1300,
        category: '交通費',
        payedById: 1,
        date: '2022-07-02',
    },
    {
        paymentName: 'ナムル',
        cost: 120,
        category: '食費',
        payedById: 1,
        date: '2022-07-05',
    },
    {
        paymentName: 'ラーメン',
        cost: 2020,
        category: '外食',
        payedById: 0,
        date: '2022-07-09',
    },
]
*/

const payments = [] as Payment[]

export const paymentState = atom<Payment[]>({
    key: 'paymentState',
    default: payments.map((payment: any, index: number) => (
    {
        id: index,
        paymentName: payment.paymentName,
        cost: payment.cost,
        category: payment.category,
        payedById: payment.payedById,
        date: payment.date,
        status: 'personal',
        onEdit: false,
        editable: true,
    })),
})

export const emptyPayments = atom<Payment[]>({
    key: 'emptyPayment',
    default:[
        {
            id: -1,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 0,
            date: '',
            status: 'personal',
            onEdit: false,
            editable: true,
        },
        {
            id: -1,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 1,
            date: '',
            status: 'personal',
            onEdit: false,
            editable: true,
        },   
    ]
})

export const defaultEmptyPayments = atom<Payment[]>({
    key: 'defaultEmptyPayments',
    default:[
        {
            id: -1,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 0,
            date: '',
            status: 'personal',
            onEdit: false,
            editable: true,
        },
        {
            id: -1,
            paymentName: '',
            cost: 0,
            category: '',
            payedById: 1,
            date: '',
            status: 'personal',
            onEdit: false,
            editable: true,
        },   
    ]
})

export const categoryState = atom<Category[]>({
    key: 'categoryState',
    default: categories.map((category) => (
    {
        id: category.id,
        name: category.name,
    })),
})

export const names = atom<string[]>({
    key: 'names',
    default: ['Yudai', 'Ami'],
})
