import { useState } from 'react'
import { useRecoilValue, useRecoilState } from 'recoil'
import { paymentState, categoryState, names, emptyPayments, defaultEmptyPayments } from '../../recoil/atoms'
import { Payment } from '../../interfaces/payment'
import {  
    TableContainer, 
    Table,
    TableHead, 
    TableBody, 
    TableRow, 
    TableCell,
    Checkbox,
    TextField
} from '@mui/material'
import { Paper } from '@mui/material'
import { Typography } from '@mui/material'
import { Button } from '@mui/material'


function CreatePayment (props: any) {
    /////////////////////////////// common import ////////////////////////////////////
    const userNames = useRecoilValue(names)
    const nameId = props.nameId
    const [payments, setPayments] = useRecoilState(paymentState)
    const [newPayments, setNewPayments] = useRecoilState(emptyPayments)
    const defaultPayments = useRecoilValue(defaultEmptyPayments)
    const [textName, setTextName] = useState('')
    //////////////////////////////////////////////////////////////////////////////////

    console.log('render',nameId)

    const handleChangedState = () => {
        if (newPayments[nameId].status === 'common') {
            setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
                if (index === nameId) {
                    return {...newPayment, status: 'personal'}
                } else { 
                    return newPayment
                }
            }))
        }
        if (newPayments[nameId].status === 'personal') {
            setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
                if (index === nameId) {
                    return {...newPayment, status: 'common'}
                } else { 
                    return newPayment
                }
            }))
        }
        ////
        console.log(newPayments)
    }

    const handleChangeName = (event: any) => {
        setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
            if (index === nameId) {
                return {...newPayment, paymentName: event.target.value}
            } else { 
                return newPayment
            }
        }))
        ////
        setTextName(event.target.value)
        console.log(newPayments)
    }

    const handleChangeCategory = (event: any) => {
        setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
            if (index === nameId) {
                return {...newPayment, category: event.target.value}
            } else { 
                return newPayment
            }
        }))
        ////
        console.log(newPayments)
    }

    const handleChangeCost = (event: any) => {
        setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
            if (index === nameId) {
                return {...newPayment, cost: event.target.value}
            } else { 
                return newPayment
            }
        }))
         ////
         console.log(newPayments)
    }

    const handleSubmit = () => {
        setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
            if (index === nameId) {
                return {...newPayment, id: payments.length}
            } else { 
                return newPayment
            }
        }))
        setPayments([...payments, newPayments[nameId]])
        setNewPayments(newPayments.map((newPayment: Payment, index: number) => {
            if (index === nameId) {
                return defaultPayments[index]
            } else { 
                return newPayment
            }
        }))
        setTextName('')
        ////
        console.log('=== submit ===')
        console.log(payments)
        console.log(emptyPayments)
    } 

    return(
        <>
            <Typography variant='h6' sx={{ mt:2 }}>NEW Pay</Typography>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell align='center'>共通</TableCell>
                            <TableCell align='center'>用途</TableCell>
                            <TableCell align='center'>分類</TableCell>
                            <TableCell align='center'>金額</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        <TableRow>
                            <TableCell key={'check'}>
                                <Checkbox 
                                    onChange={handleChangedState}
                                />
                            </TableCell>
                            <TableCell align='center' key='name'>
                                <TextField onChange={handleChangeName} value={textName}></TextField>
                            </TableCell>
                            <TableCell align='center' key='category'>
                                <TextField onChange={handleChangeCategory}></TextField>
                            </TableCell>
                            <TableCell align='center' key='cost'>                                        
                                <TextField onChange={handleChangeCost}></TextField>
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell>
                                <Button
                                    sx={{ 
                                        display: 'block',
                                        textAlign: 'ceter',
                                        backgroundColor: 'blue',
                                        width: 1/1
                                    }}
                                    onClick={handleSubmit}
                                >
                                    追加
                                </Button>
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    )
}

export default CreatePayment
