import { useState } from 'react'
import { useRecoilValue, useRecoilState } from 'recoil'
import { paymentState, categoryState, names, emptyPayments } from '../../recoil/atoms'
import { Payment } from '../../interfaces/payment'
import {  
    TableContainer, 
    Table,
    TableHead, 
    TableBody, 
    TableRow, 
    TableCell,
    Checkbox,
    TextField
} from '@mui/material'
import { Paper } from '@mui/material'
import { Typography } from '@mui/material'
import { Button } from '@mui/material'


function CreatePayment (props: any) {
    /////////////////////////////// common import ////////////////////////////////////
    const userNames = useRecoilValue(names)
    const nameId = props.nameId
    const [payments, setPayments] = useRecoilState(paymentState)
    const [newPayments, setNewPayments] = useRecoilState(emptyPayments)
    //////////////////////////////////////////////////////////////////////////////////

    console.log(nameId)

    const handleChangedState = () => {
        if (newPayments[nameId].status === 'common') {
            setNewPayments([newPayments[0], {...newPayments[1], status: 'personal'}])
        }
        if (newPayments[nameId].status === 'personal') {
            setNewPayments([newPayments[0], {...newPayments[1], status: 'common'}])
        }
        ////
        console.log(newPayments)
    }

    const handleChangeName = (event: any) => {
        setNewPayment({...newPayment, paymentName: event.target.value})
        ////
        console.log(newPayment)
    }

    const handleChangeCategory = (event: any) => {
        setNewPayment({...newPayment, category: event.target.value})
    }

    const handleChangeCost = (event: any) => {
        setNewPayment({...newPayment, cost: event.target.value})
    }

    const handleSubmit = () => {
        setPayments([...payments, newPayment])
        setNewPayment(emptyPayment)
        ////
        console.log(payments)
        console.log(emptyPayment)
    } 

    return(
        <>
            <Typography variant='h6' sx={{ mt:2 }}>NEW Pay</Typography>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell align='center'>共通</TableCell>
                            <TableCell align='center'>用途</TableCell>
                            <TableCell align='center'>分類</TableCell>
                            <TableCell align='center'>金額</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        <TableRow>
                            <TableCell>
                                <Checkbox 
                                    onChange={() => handleChangedState}
                                />
                            </TableCell>
                            <TableCell align='center'>
                                <TextField onChange={() => handleChangeName}></TextField>
                            </TableCell>
                            <TableCell align='center'>
                                <TextField onChange={() => handleChangeCategory}></TextField>
                            </TableCell>
                            <TableCell align='center'>                                        
                                <TextField onChange={() => handleChangeCost}></TextField>
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <Button
                                sx={{ 
                                    display: 'block',
                                    textAlign: 'ceter',
                                    backgroundColor: 'blue',
                                    width: 1/1
                                }}
                                onChange={() => handleSubmit}
                            >
                                追加
                            </Button>
                        </TableRow>
                    </TableBody>
                </Table>
            </TableContainer>
        </>
    )
}

export default CreatePayment
